#include <cstdio>#include <cctype>char words[26][7][7], str[10000];int word_index[10000] = {0};int main() {	for(int i = 0; i < 26; i++) {		for(int j = 0; j < 7; j++) {			scanf("%s", words[i][j]);		}	}	getchar();	fgets(str, 10000, stdin);	int count = 0;	for(int i = 0; str[i] != '\0'; i++) {		if(isupper(str[i])) {			word_index[count++] = str[i] - 'A';		} else {			word_index[count++] = -1;			while(!isupper(str[i]) && str[i] != '\0') i++;			i--;		}	}	int i, j, k;	while(word_index[i] == -1) i++;	for(; i < count; i++) {		for(j = 0; j < 7; j++) {			printf("%s", words[word_index[i]][j]);			for(k = i + 1; word_index[k] != -1; k++) {				printf(" %s", words[word_index[k]][j]);			}			printf("\n");		}		i = k;		if(i < count - 1) printf("\n");	}	return 0;}